        public ALCSA.Entidades.Cobranzas.DocumentoEstandard2 Buscar(int id, int idCobranza)
        {
            ALCSA.FWK.BD.Servicio objServicio = new ALCSA.FWK.BD.Servicio();
            objServicio.Conexion = Conexion.ALCSA;
            objServicio.Comando = "dbo.SPALC_DOCUESTANDARD2_BUSCAR";
            objServicio.Parametros.Add(new ALCSA.FWK.BD.Parametro() { Nombre = "@INT_IdDocuEstan2", Valor = id, Direccion = ALCSA.FWK.BD.Enumeradores.Direcciones.Entrada });
            objServicio.Parametros.Add(new ALCSA.FWK.BD.Parametro() { Nombre = "@INT_IdCobranza", Valor = idCobranza, Direccion = ALCSA.FWK.BD.Enumeradores.Direcciones.Entrada });
            IList<ALCSA.Entidades.Cobranzas.DocumentoEstandard2> arrRespuesta = objServicio.Ejecutar<ALCSA.Entidades.Cobranzas.DocumentoEstandard2>();
            return arrRespuesta.Count > 0 ? arrRespuesta[0] : null;
        }

