        public ALCSA.Entidades.Cobranzas.DocumentoJuicio Buscar(int idDocumentoJuicio, int idCobranza)
        {
            ALCSA.FWK.BD.Servicio objServicio = new ALCSA.FWK.BD.Servicio();
            objServicio.Conexion = Conexion.ALCSA;
            objServicio.Comando = "dbo.SPALC_DOCUJUICIO_BUSCAR";
            objServicio.Parametros.Add(new ALCSA.FWK.BD.Parametro() { Nombre = "@INT_IdDocuJuicio", Valor = idDocumentoJuicio, Direccion = ALCSA.FWK.BD.Enumeradores.Direcciones.Entrada });
            objServicio.Parametros.Add(new ALCSA.FWK.BD.Parametro() { Nombre = "@INT_IdCobranza", Valor = idCobranza, Direccion = ALCSA.FWK.BD.Enumeradores.Direcciones.Entrada });
            IList<ALCSA.Entidades.Cobranzas.DocumentoJuicio> arrRespuesta = objServicio.Ejecutar<ALCSA.Entidades.Cobranzas.DocumentoJuicio>();
            return arrRespuesta.Count > 0 ? arrRespuesta[0] : null;
        }

