        public ALCSA.Entidades.Cobranzas.DocumentoPagare Buscar(int idDocumento, int idCobranza)
        {
            ALCSA.FWK.BD.Servicio objServicio = new ALCSA.FWK.BD.Servicio();
            objServicio.Conexion = Conexion.ALCSA;
            objServicio.Comando = "dbo.SPALC_DOCUPAGARE_BUSCAR";
            objServicio.Parametros.Add(new ALCSA.FWK.BD.Parametro() { Nombre = "@INT_IdDocuPagare", Valor = idDocumento, Direccion = ALCSA.FWK.BD.Enumeradores.Direcciones.Entrada });
            objServicio.Parametros.Add(new ALCSA.FWK.BD.Parametro() { Nombre = "@INT_IdCobranza", Valor = idCobranza, Direccion = ALCSA.FWK.BD.Enumeradores.Direcciones.Entrada });

            IList<ALCSA.Entidades.Cobranzas.DocumentoPagare> arrRespuesta = objServicio.Ejecutar<ALCSA.Entidades.Cobranzas.DocumentoPagare>();
            return arrRespuesta.Count > 0 ? arrRespuesta[0] : null;
        }

