            objServicio.Conexion = Conexion.ALCSA;
            objServicio.Comando = "dbo.SPALC_DOCUPAGARE_ELIMINAR";
            objServicio.Parametros.Add(new ALCSA.FWK.BD.Parametro() { Nombre = "@INT_IdDocuPagare", Valor = id, Direccion = ALCSA.FWK.BD.Enumeradores.Direcciones.Entrada });
            objServicio.EjecutarSinRetorno();
        }

